const processData = ( input ) => {
    try {
        // Parse JSON
        let attendanceData;
        if ( typeof input === 'string' ) {
            attendanceData = JSON.parse( input )
        } else {
            attendanceData = input;
        }

        if ( !attendanceData.data || !Array.isArray( attendanceData.data ) || attendanceData.data.length === 0 ) {
            return "No"
        }

        let summaryParts = []
        summaryParts.push( "Thời gian check in/out:" )
        summaryParts.push( "" )

        attendanceData.data.forEach( ( record, index ) => {
            summaryParts.push( `Ngày: ${ record.date }` )

            let checkinText = "check in time: "
            if ( record.checkin_time ) {
                checkinText += record.checkin_time
            } else {
                checkinText += "không có"
            }
            summaryParts.push( checkinText )


            let checkoutText = "check out time: "
            if ( record.checkout_time ) {
                checkoutText += record.checkout_time
            } else {
                checkoutText += "không có"
            }
            summaryParts.push( checkoutText )

            if ( index < attendanceData.data.length - 1 ) {
                summaryParts.push( "" ) // Add empty line between records
            }
        } )

        // Join all parts into final summary string
        const finalSummary = summaryParts.join( '\n' )

        return finalSummary

    } catch ( error ) {
        return "No"
    }
}

return processData( $input )

