const processData = ( input ) => {
    try {
        // Parse JSON
        let data
        if ( typeof input === 'string' ) {
            data = JSON.parse( input )
        } else {
            data = input;
        }

        if ( !data.data || !Array.isArray( data.data ) || data.data.length === 0 ) {
            return "No"
        }

        // Initialize variables to track different types of work day statistics
        let totalAttendance = 0
        let fullDays = 0         
        let halfDays = 0         
        let totalWorkingDays = 0 

        let fullDayDates = []
        let halfDayDates = []

        data.data.forEach( ( record ) => {
            totalAttendance += record.attendance
            totalWorkingDays += 1

            if ( record.attendance === 1 ) {
                fullDays += 1
                fullDayDates.push( record.date )
            } else if ( record.attendance === 0.5 ) {
                halfDays += 1
                halfDayDates.push( record.date )
            }
            // Note: We could add more categories here for other attendance values if needed
        } )

        let summaryParts = []

        summaryParts.push( `Tổng số ngày công: ${ totalAttendance }` )
        summaryParts.push( "" )
        summaryParts.push( `Tổng số ngày đi làm: ${ totalWorkingDays }` )
        summaryParts.push( "" )


        summaryParts.push( `Số ngày làm đủ công (1.0): ${ fullDays }` )
        fullDayDates.forEach( ( date, index ) => {
            summaryParts.push( `  ${ index + 1 }. ${ date }` )
        } )
        summaryParts.push( "" )


        summaryParts.push( `Số ngày làm nửa công (0.5): ${ halfDays }` )
        halfDayDates.forEach( ( date, index ) => {
            summaryParts.push( `  ${ index + 1 }. ${ date }` )
        } )


        // Join all parts into final summary string
        const finalSummary = summaryParts.join( '\n' )

        return finalSummary

    } catch ( error ) {
        return "No"
    }
}

return processData( $input )

